exports.load = function(html) {
  const src = html.toString().toLowerCase();
  return function(selector) {
    const m = selector.toLowerCase().match(/^[a-z0-9]+/);
    const tag = m ? m[0] : selector.toLowerCase();
    let idx = src.indexOf('<' + tag);
    let count = 0;
    while (idx !== -1) {
      count++;
      idx = src.indexOf('<' + tag, idx + 1);
    }
    return { length: count };
  };
};
